code:
set color purple
set size 5
repeat 3 {
  draw circle x=10+$i*10 y=10
}

set color orange
repeat 5 {
  set size 3+$i
  draw circle x=$i*8 y=$i*5
}

set color gold
repeat 12 {
  set size 3
  draw circle x=cos($i*30)*40 y=sin($i*30)*40
}

var mode on
set color red
if mode is on {
  draw circle x=10 y=10
} else {
  draw square x=10 y=10
}

define ring(r) {
  set size 3
  repeat 8 {
    draw circle x=cos($i*45)*r y=sin($i*45)*r
  }
}
call ring(30)
